const express = require('node-media-server/api/routes/node_modules/node-media-server/api/routes/node_modules/node-media-server/api/routes/node_modules/express');
const relayController = require('node-media-server/api/routes/node_modules/node-media-server/api/routes/node_modules/node-media-server/api/controllers/relay');

module.exports = (context) => {
  let router = express.Router();
  router.post('/pull', relayController.pullStream.bind(context));
  router.post('/push', relayController.pushStream.bind(context));
  return router;
};
