const express = require('node-media-server/api/routes/node_modules/node-media-server/api/routes/node_modules/node-media-server/api/routes/node_modules/express');
const streamController = require('node-media-server/api/routes/node_modules/node-media-server/api/routes/node_modules/node-media-server/api/controllers/streams');

module.exports = (context) => {
  let router = express.Router();
  router.get('/', streamController.getStreams.bind(context));
  router.get('/:app/:stream', streamController.getStream.bind(context));
  return router;
};
